CREATE TABLE `questions` (
  `question_id` int NOT NULL AUTO_INCREMENT,
  `parent_question_id` int DEFAULT NULL COMMENT 'If created by cloning/editing another question, points to that question_id',
  `created_from_exam_id` int DEFAULT NULL COMMENT 'If created while cloning from an exam, optional trace to exam_id',
  `batch_id` int DEFAULT NULL COMMENT 'Links each question to its batch (CSV upload or exam clone)',
  `create_type` enum('BULK_CSV','MANUAL','FROM_EXAM') COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'BULK_CSV' COMMENT 'Source of creation, default BULK_CSV',
  `csv_serial_no` int DEFAULT NULL COMMENT 'Optional serial number inside CSV (1..N)',
  `csv_exam_type` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'Exam type label from CSV (e.g., mock1, practice)',
  `provider_id` int DEFAULT NULL,
  `provider_name` varchar(150) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `certification_id` int DEFAULT NULL,
  `certification_name` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `exam_type` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'Logical exam type e.g., mock1, practice, final',
  `question_text` text COLLATE utf8mb4_general_ci NOT NULL,
  `option_a` text COLLATE utf8mb4_general_ci,
  `option_b` text COLLATE utf8mb4_general_ci,
  `option_c` text COLLATE utf8mb4_general_ci,
  `option_d` text COLLATE utf8mb4_general_ci,
  `option_e` text COLLATE utf8mb4_general_ci,
  `option_f` text COLLATE utf8mb4_general_ci,
  `question_type` enum('single_choice','multiple_choice','true_false','drag_and_drop') COLLATE utf8mb4_general_ci DEFAULT 'single_choice',
  `correct_options` varchar(50) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'E.g., "A" or "A,B"',
  `correct_explanation` text COLLATE utf8mb4_general_ci,
  `incorrect_explanation` text COLLATE utf8mb4_general_ci,
  `domain` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `topic` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `subtopic` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `difficulty_level` varchar(50) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `marks` float DEFAULT '1',
  `negative_marks` float DEFAULT '0',
  `primary_tags` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `secondary_tags` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `tertiary_tags` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `is_mandatory` tinyint(1) DEFAULT '0' COMMENT 'Important question; analytics can use this',
  `is_future` tinyint(1) DEFAULT '0' COMMENT 'Research-only; not used in live scoring',
  `question_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `option_a_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `option_b_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `option_c_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `option_d_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `option_e_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `option_f_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `correct_explanation_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `incorrect_explanation_image_path` varchar(255) COLLATE utf8mb4_general_ci DEFAULT NULL,
  `number_of_options` int DEFAULT NULL,
  `number_of_correct_options` int DEFAULT NULL,
  `status` enum('Active','Inactive') COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'Inactive' COMMENT 'Only Active questions are available to exams',
  `error_status` varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'Transient validation code (e.g., InvalidImagePath, MissingField)',
  `error_message` text COLLATE utf8mb4_general_ci COMMENT 'Human readable error message; cleared on fix',
  `content_hash` char(64) COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT 'SHA256(normalized content+provider+cert+exam_type) - optional unique guard',
  `created_by` int DEFAULT NULL,
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_by` int DEFAULT NULL,
  `updated_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`question_id`),
  KEY `fk_questions_parent` (`parent_question_id`),
  KEY `idx_q_provider` (`provider_id`),
  KEY `idx_q_cert` (`certification_id`),
  KEY `idx_q_status` (`status`),
  KEY `idx_q_examtype` (`exam_type`),
  KEY `idx_q_topic` (`topic`),
  KEY `idx_q_contenthash` (`content_hash`),
  KEY `idx_q_batch` (`batch_id`),
  CONSTRAINT `fk_questions_batch` FOREIGN KEY (`batch_id`) REFERENCES `batches` (`batch_id`) ON DELETE SET NULL,
  CONSTRAINT `fk_questions_parent` FOREIGN KEY (`parent_question_id`) REFERENCES `questions` (`question_id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=751 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
