INSERT INTO `sections` VALUES (25,42,'HERO','About GenoSpark Hero',NULL,'IMAGE','https://ik.imagekit.io/genospark/section_media_1759650979291_gs_about_192L6Kw5X.png',NULL,1,1,NULL,NULL,NULL,NULL,4,4,'2025-10-02 14:43:13','2025-10-05 07:57:48'),(26,42,'MIDDLE','Who We Are','<section class=\"py-12 px-6 max-w-6xl mx-auto\">\n    <h2 class=\"text-3xl font-bold mb-6\">Who We Are</h2>\n    <div class=\"space-y-4 text-lg leading-relaxed\">\n      <p>\n        Genospark Innovative Solutions Pvt. Ltd. is a forward-thinking EdTech\n        and cloud consulting start-up dedicated to bridging the gap between\n        academic knowledge and industry-ready skills. Our AI-driven platform\n        offers scalable solutions to upskill students, professionals, and\n        organizations in cutting-edge cloud technologies, including AWS,\n        Azure, GCP, Snowflake, and Oracle.\n      </p>\n      <p>\n        Our commitment extends to learners across all backgrounds—enabling\n        engineering graduates to acquire globally recognized certifications\n        and secure high-paid positions in top multinational companies,\n        empowering individual professionals to elevate their careers, and\n        supporting corporate teams in building agile, cloud-ready talent. We\n        strive to become the preferred upskilling partner for both academic\n        institutions and corporate enterprises, delivering high-impact\n        learning through practical training, curated content, and affordable,\n        scalable pricing.\n      </p>\n      <p>\n        At Genospark, we go beyond certifications and job placements. Our\n        mission is to be a lifelong partner in every learner\'s professional\n        journey. Through a strong alumni network and a vibrant community of\n        associates, we foster ongoing mentorship, collaboration, and mutual\n        growth. We are not just preparing learners for immediate success—we\n        are investing in what they can become over the next decade and\n        beyond, with a shared vision of enabling continuous career\n        advancement, recognition, and leadership in the global tech\n        landscape.\n      </p>\n    </div>\n  </section>',NULL,NULL,NULL,2,1,NULL,NULL,NULL,NULL,4,4,'2025-10-02 14:45:31','2025-10-02 14:46:21'),(27,42,'MIDDLE','Mission & Vision','<section class=\"py-12 px-6 max-w-6xl mx-auto grid md:grid-cols-2 gap-8\">\n    <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n      <h2 class=\"text-2xl font-semibold mb-2\">Mission</h2>\n      <p>\n        Empowering aspiring individuals to secure high-paying, reputable\n        careers globally.\n      </p>\n    </div>\n    <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n      <h2 class=\"text-2xl font-semibold mb-2\">Vision</h2>\n      <p>\n        Building an evolving global community powered by AI and cloud-ready\n        talent, impacting and uplifting society.\n      </p>\n    </div>\n  </section>',NULL,NULL,NULL,3,1,NULL,NULL,NULL,NULL,4,4,'2025-10-02 14:46:28','2025-10-02 14:47:23'),(28,42,'MIDDLE','Why GenoSpark?','<section class=\"py-12 px-6 max-w-6xl mx-auto\">\n    <h2 class=\"text-3xl font-bold text-center mb-10\">Why GenoSpark?</h2>\n    <div class=\"grid md:grid-cols-2 lg:grid-cols-3 gap-6\">\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"font-semibold text-xl mb-2\">Accelerated Curated Content (ACC)</h3>\n        <p>Practical, streamlined learning designed for real-world skills and industry\n  exams.</p>\n      </div>\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"font-semibold text-xl mb-2\">Salary Augmentation Program (SAP)</h3>\n        <p>Unique, customized training to help you earn higher salaries and climb the career\n  ladder.</p>\n      </div>\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"font-semibold text-xl mb-2\">Hands-On Training</h3>\n        <p>Immersive, project-based learning to build job-ready expertise.</p>\n      </div>\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"font-semibold text-xl mb-2\">AI-Powered Support</h3>\n        <p>Personalized guidance and scalable solutions to boost your success.</p>\n      </div>\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"font-semibold text-xl mb-2\">Alumni & Community Network</h3>\n        <p>A thriving ecosystem for mentorship, collaboration, and lifelong growth.</p>\n      </div>\n    </div>\n  </section>',NULL,NULL,NULL,4,1,NULL,NULL,NULL,NULL,4,4,'2025-10-02 14:47:30','2025-10-02 14:48:25'),(29,42,'MIDDLE','Services Offered','<section class=\"py-12 px-6 max-w-6xl mx-auto\">\n    <h2 class=\"text-3xl font-bold text-center mb-10\">Services Offered</h2>\n    <div class=\"grid md:grid-cols-3 gap-8\">\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"text-xl font-semibold mb-3\">For Individuals</h3>\n        <ul class=\"list-disc list-inside space-y-2\">\n          <li>Mock Interviews (Personalized feedback to boost your confidence and skills)</li>\n          <li>Internship Opportunities (Paid and unpaid, online and offline, with real-world\n  experience)</li>\n          <li>Salary Augmentation Program (Customized training to help you secure higher-paying\n   roles)</li>\n          <li>Hands-on Cloud & AI Training (AWS, Azure, GCP, Snowflake, Oracle, and more)</li>\n        </ul>\n      </div>\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"text-xl font-semibold mb-3\">For Universities/Colleges</h3>\n        <ul class=\"list-disc list-inside space-y-2\">\n          <li>Campus Training & Placement Support</li>\n          <li>Certification Enablement & Practical Skill Development</li>\n          <li>Collaborative Workshops and Webinars</li>\n          <li>Internship Tie-ups for Students</li>\n        </ul>\n      </div>\n      <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-6\">\n        <h3 class=\"text-xl font-semibold mb-3\">For Corporates</h3>\n        <ul class=\"list-disc list-inside space-y-2\">\n          <li>Cloud Consulting (Architecture review, design, and implementation support)</li>\n          <li>Customized Corporate Training Programs</li>\n          <li>Talent Enablement & Certification Support</li>\n          <li>Employee Upskilling and Reskilling</li>\n        </ul>\n      </div>\n    </div>\n  </section>',NULL,NULL,NULL,5,1,NULL,NULL,NULL,NULL,4,4,'2025-10-02 14:48:32','2025-10-02 14:50:35'),(30,42,'MIDDLE','Leadership Team','<section class=\"py-12 px-6 max-w-6xl mx-auto\">\n  <h2 class=\"text-3xl font-bold mb-6 text-center\">Leadership Team</h2>\n  <div class=\"bg-white dark:bg-gray-800 rounded-2xl shadow-md p-8 max-w-2xl mx-auto space-y-4\">\n    <h3 class=\"text-xl font-semibold mb-2 text-center\">Sathiya Sai</h3>\n    <p class=\"italic text-center text-gray-600 dark:text-gray-400\">Founder of GenoSpark Innovative Solutions</p>\n    \n    <div class=\"space-y-4 text-left pt-4\">\n      <p>\n        Sathiya Sai is an industry veteran with over 30 years of experience,\n        including leadership roles in top multinational companies worldwide.\n        He holds multiple AWS certifications (5x Certified), is a certified\n        Project Management Professional (PMP), and an Agile Practitioner.\n      </p>\n      <p>\n        Driven by a passion to empower the next generation, Sathiya has\n        developed unique programs such as the Accelerated Curated Course\n        Content (ACC) and the Salary Augmentation Program (SAP), designed to\n        fast-track skills and career growth.\n      </p>\n      <p>\n        GenoSpark strives to nurture engineering graduates and professionals\n        by guiding them toward globally recognized cloud certifications. Our\n        mission is to equip learners with practical skills that enable them\n        to secure highly sought-after roles in reputed multinational\n        companies.\n      </p>\n      <p>\n        In our inaugural year, we aim to help over 1,000 candidates achieve\n        certification and career success, with a commitment to steadily\n        increase this impact and foster continuous growth in the years\n        ahead.\n      </p>\n      <p>\n        GenoSpark is committed to building a thriving alumni community that\n        fosters continuous learning, mentorship, and professional success\n        for individuals and organizations.\n      </p>\n    </div>\n  </div>\n</section>',NULL,NULL,NULL,6,1,NULL,NULL,NULL,NULL,4,4,'2025-10-02 14:50:42','2025-10-03 09:37:16'),(36,44,'HERO','Career Opportunities','<link href=\"https://fonts.googleapis.com/icon?family=Material+Icons\" \n  rel=\"stylesheet\" />\n ','IMAGE','https://ik.imagekit.io/genospark/section_media_1759654585107_gs_career_1920_93EngfTeQ.png',NULL,1,1,NULL,NULL,NULL,NULL,4,4,'2025-10-03 14:12:46','2025-10-27 15:39:00'),(37,44,'MIDDLE','Faculty Positions','<div class=\"max-w-4xl mx-auto px-6\" style=\"margin-top: 3rem;\">\n    <section class=\"bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 border \n  border-gray-100 dark:border-gray-700\">\n      <div class=\"flex items-start gap-4\">\n        <div class=\"bg-blue-100 dark:bg-blue-900 p-2 rounded-lg\">\n          <span class=\"material-icons text-blue-600 dark:text-blue-400\" \n  style=\"font-size: 28px;\">school</span>\n        </div>\n        <div>\n          <h2 class=\"text-2xl font-bold text-gray-900 dark:text-white mb-2\">Faculty    \n   Positions</h2>\n          <p class=\"text-gray-600 dark:text-gray-300 leading-relaxed\">\n            Experienced professionals and educators in cloud and AI technologies       \n  are invited to join our teaching and content development team.\n          </p>\n        </div>\n      </div>\n    </section>\n  </div>',NULL,NULL,NULL,2,1,